/*==============================
	Fonts
==============================*/
@import url("https://fonts.googleapis.com/css2?family=Montserrat:ital,wght@0,400;0,500;1,400&amp;family=Roboto:wght@400;500&amp;display=swap");
/*==============================
	Common styles
==============================*/
html {
  -webkit-text-size-adjust: 100%;
  -ms-text-size-adjust: 100%;
}
html,
body {
  height: 100%;
}
body {
  font-family: 'Roboto', sans-serif;
  font-weight: 400;
  background-color: #fff;
  -webkit-font-smoothing: antialiased;
}
button {
  padding: 0;
  border: none;
  background-color: transparent;
  transition: 0.4s ease;
  transition-property: color, background-color, border-color, box-shadow;
  cursor: pointer;
}
button:focus {
  outline: none;
}
a {
  transition: 0.4s ease;
  transition-property: color, background-color, border-color, box-shadow;
}
a:hover,
a:active,
a:focus {
  outline: none;
  text-decoration: none;
}
input,
textarea,
select {
  padding: 0;
  margin: 0;
  border-radius: 0;
  -webkit-appearance: none;
  -moz-appearance: none;
  -ms-appearance: none;
  appearance: none;
  box-shadow: none;
  transition: 0.4s ease;
  transition-property: color, background-color, border-color, box-shadow;
}
input:focus,
textarea:focus,
select:focus {
  outline: none;
}
select::-ms-expand {
  display: none;
}
ul {
  margin: 0;
  padding: 0;
  list-style: none;
}
::-moz-selection {
  background: #444;
  color: #fff;
  text-shadow: none;
}
::selection {
  background: #444;
  color: #fff;
  text-shadow: none;
}
::-webkit-input-placeholder {
  color: #78b0ef;
  opacity: 1;
}
::-moz-placeholder {
  color: #78b0ef;
  opacity: 1;
}
:-moz-placeholder {
  color: #78b0ef;
  opacity: 1;
}
:-ms-input-placeholder {
  color: #78b0ef;
  opacity: 1;
}
:focus {
  outline: -webkit-focus-ring-color auto 0px;
}
/* owl carousel dots customized */
.owl-dots {
  position: relative;
  width: 100%;
  text-align: center;
  -webkit-tap-highlight-color: transparent;
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  margin-top: 40px;
}
.owl-dots .owl-dot {
  display: block;
  position: relative;
  margin: 0 8px;
  width: 14px;
  height: 14px;
}
.owl-dots .owl-dot span {
  display: block;
  position: absolute;
  width: 6px;
  height: 6px;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
