/*==============================
	Header
==============================*/
.header {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  height: 70px;
  padding: 0 15px;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  background-color: transparent;
  z-index: 9;
  transition: 0.4s ease;
  transition-property: background-color, box-shadow;
}
.header__menu {
  height: 40px;
  width: 40px;
  position: absolute;
  right: 15px;
  display: block;
  cursor: pointer;
  z-index: 9;
}
.header__menu i {
  position: absolute;
  top: 0;
  left: 0;
