  opacity: 1;
  font-size: 20px;
  color: #fff;
}
.header__menu--active i.ti-menu {
  font-size: 0;
  opacity: 0;
}
.header__nav {
  position: fixed;
  width: 280px;
  right: 0;
  top: 0;
  bottom: 0;
  background-color: #444;
  padding: 70px 30px 0;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: flex-start;
  border-bottom: 1px solid rgba(255,255,255,0.1);
