}
.header__nav li {
  margin-bottom: 15px;
}
.header__nav li:last-child {
  margin-bottom: 0;
}
.header__nav a {
  color: #fff;
  font-size: 16px;
  letter-spacing: 0.6px;
}
.header__nav a i {
  color: #fff;
  transition: 0.3s ease;
}
.header__nav--active {
  transform: translate3d(0, 0, 0);
}
.header__dropdown {
  position: relative;
  display: block;
  width: 100%;
}
.header__dropdown .dropdown-toggle {
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: center;
}
