  font-size: 8px;
  margin-top: 3px;
  margin-left: 4px;
}
.header__dropdown .dropdown-toggle[aria-expanded="true"] i {
  transform: rotate(-180deg);
}
.header__dropdown-menu {
  position: absolute;
  background-color: #fff;
  padding: 20px 25px;
  display: block;
  width: 100%;
  text-align: left;
  z-index: -1;
  pointer-events: none;
  opacity: 0;
  border-radius: 12px;
  box-shadow: 0 16px 56px 0 rgba(0,0,0,0.1);
  transition: 0.3s ease;
  margin-top: 0;
  top: 100%;
}
.header__dropdown-menu li {
  padding: 0;
  margin-bottom: 10px;
}
.header__dropdown-menu li:last-child {
  margin-bottom: 0;
}
.header__dropdown-menu a {
  font-size: 16px;
  color: #444;
}
.header__dropdown-menu.show {
  z-index: 1000;
  pointer-events: auto;
  opacity: 1;
  margin-top: 15px;
}
.header--white {
  background-color: #fff;
}
.header--active {
  background-color: #fff;
  box-shadow: 0 6px 26px 0 rgba(0,0,0,0.1);
}
