.form {
  margin: 30px auto 0;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  background-color: #fff;
  box-shadow: 0 16px 56px 0 rgba(0,0,0,0.1);
  border-radius: 12px;
  padding: 20px;
  max-width: 450px;
  position: relative;
}
.form__input,
.form__select,
.form__textarea {
  display: block;
  width: 100%;
  border-radius: 30px;
  height: 56px;
  border: none;
  background-color: rgba(120,176,239,0.25);
  color: #444;
  padding: 0 20px;
  margin-bottom: 20px;
  font-size: 16px;
  font-weight: 400;
}
.form__textarea {
  resize: none;
  height: 140px;
  padding: 15px 20px;
}
.form__select-wrap {
  display: block;
  position: relative;
}
.form__select-wrap:before {
  content: '\e64b';
  position: absolute;
  display: block;
  font-family: 'themify';
  top: 25px;
  right: 20px;
  color: #78b0ef;
  pointer-events: none;
  font-size: 10px;
  height: 8px;
  line-height: 8px;
}
.form__select {
  cursor: pointer;
  color: #78b0ef;
}
.form__group {
  position: relative;
  margin-top: 30px;
}
.form__group input:not(:checked),
.form__group input:checked {
  position: absolute;
  left: -9999px;
}
.form__group input:not(:checked) + label,
.form__group input:checked + label {
  font-size: 16px;
  color: #707070;
  position: relative;
  cursor: pointer;
  padding-left: 34px;
  line-height: 22px;
  margin: 0;
}
.form__group input:not(:checked) + label a,
.form__group input:checked + label a {
  color: #707070;
  text-decoration: underline;
}
.form__group input:not(:checked) + label a:hover,
.form__group input:checked + label a:hover {
  color: #78b0ef;
  text-decoration: underline;
}
.form__group input:not(:checked) + label:before,
.form__group input:checked + label:before {
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  width: 22px;
  height: 22px;
  background-color: #fff;
  border-radius: 6px;
  background-color: rgba(120,176,239,0.25);
}
.form__group input:not(:checked) + label:after,
.form__group input:checked + label:after {
  font-family: 'themify';
  content: '\e64c';
  position: absolute;
  left: 0;
  top: 0;
  width: 22px;
  height: 22px;
  font-size: 12px;
  line-height: 22px;
  text-align: center;
  transition: 0.3s;
  color: #78b0ef;
}
.form__group input:not(:checked) + label:after {
  opacity: 0;
  transform: scale(0);
}
.form__group input:checked + label:after {
  opacity: 1;
  transform: scale(1);
}
.form--contacts {
  overflow: hidden;
  width: 100%;
}
.form--contacts:after {
  content: '';
  position: absolute;
  display: block;
  left: 0;
  right: 0;
  height: 6px;
  background-color: #78b0ef;
  bottom: 0;
}
.form--contacts-page {
  max-width: 100%;
  margin: 30px 0 0;
}
@media (min-width: 768px) {
  .form {
    padding: 40px 40px;
  }
  .form__group {
    margin-top: 40px;
  }
}
@media (min-width: 1200px) {
  .form--contacts-page {
    margin-right: 30px;
    width: auto;
  }
}
/*==============================
	Sign
==============================*/
.sign {
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: auto;
  min-height: 100vh;
  position: relative;
}
.sign__content {
  border-radius: 12px;
  background-color: #fff;
  box-shadow: 0 16px 56px 0 rgba(0,0,0,0.1);
  width: 100%;
  max-width: 290px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin: 20px 0;
  position: relative;
  z-index: 2;
  overflow: hidden;
  width: 100%;
}
.sign__content:after {
  content: '';
  position: absolute;
  display: block;
  left: 0;
  right: 0;
  height: 6px;
  background-color: #78b0ef;
  bottom: 0;
}
.sign__form {
  width: 100%;
  padding: 30px 20px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}
.sign__form .btn {
  max-width: 100%;
}
.sign__error {
  padding: 30px 20px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
}
.sign__error h1 {
  font-family: 'Montserrat', sans-serif;
  color: #444;
  font-size: 100px;
  font-weight: 500;
  margin-bottom: 0;
}
.sign__error p {
  font-size: 16px;
  line-height: 24px;
  color: #707070;
  margin-bottom: 30px;
  width: 100%;
  text-align: center;
}
.sign__logo {
  margin-bottom: 30px;
}
.sign__forgot {
  color: #757575;
  font-size: 16px;
  margin-top: 30px;
  text-decoration: underline;
}
.sign__forgot:hover {
  color: #78b0ef;
  text-decoration: underline;
}
.sign__box {
  width: 100%;
  text-align: center;
  padding: 20px 0 26px;
  border-top: 1px solid rgba(0,0,0,0.08);
}
.sign__box p {
  font-size: 16px;
  line-height: 24px;
  margin: 0;
  color: #707070;
}
.sign__box a {
  color: #707070;
  text-decoration: underline;
}
.sign__box a:hover {
  color: #78b0ef;
  text-decoration: underline;
}
.sign__particles {
  position: absolute;
  display: block;
  top: 0;
  right: 0;
  bottom: 10px;
  left: 0;
  z-index: 1;
}
@media (min-width: 360px) {
  .sign__content {
    max-width: 330px;
  }
}
@media (min-width: 576px) {
  .sign__content {
    max-width: 400px;
  }
  .sign__form {
    padding: 40px;
  }
  .sign__error {
    padding: 40px;
  }
  .sign__forgot {
    margin-top: 40px;
  }
  .sign__logo {
    margin-bottom: 40px;
  }
}
/*==============================
	Partners
==============================*/
.partners {
  display: block;
  position: relative;
  padding: 50px 0;
  background-color: #fff;
}
.partners__slider img {
  max-width: 100%;
  opacity: 0.7;
  -webkit-filter: grayscale(1);
  filter: grayscale(1);
  transition: 0.5s ease;
}
.partners__slider a:hover img {
  -webkit-filter: grayscale(0);
  filter: grayscale(0);
  opacity: 1;
}
@media (min-width: 768px) {
  .partners {
    padding: 55px 0;
  }
}
/*==============================
	Contacts
==============================*/
.contacts__list {
  margin-bottom: 30px;
}
.contacts__list li {
  margin-bottom: 10px;
}
.contacts__list li:last-child {
  margin-bottom: 0;
}
.contacts__list a {
  font-size: 16px;
  color: #757575;
  font-family: 'Montserrat', sans-serif;
  font-weight: 500;
}
.contacts__list a:hover {
  color: #78b0ef;
}
.contacts__social {
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: center;
  width: 100%;
}
.contacts__social svg {
  width: 30px;
  height: auto;
  transition: opacity 0.4s ease;
}
.contacts__social li {
  margin-right: 15px;
}
.contacts__social li:last-child {
  margin-left: 0;
}
.contacts__social a {
  font-size: 17px;
  color: #757575;
}
.contacts__social a:hover {
  color: #78b0ef;
}
.contacts__social a:hover svg {
  opacity: 0.7;
}
/*==============================
	Footer
==============================*/
.footer {
  background-color: #fff;
  display: block;
  position: relative;
  overflow: hidden;
  padding: 50px 0 30px;
}
.footer__logo {
  margin-bottom: 15px;
}
.footer__title {
  color: #444;
  font-size: 16px;
  font-weight: 500;
  font-family: 'Montserrat', sans-serif;
  margin-bottom: 20px;
}
.footer__list {
  margin-bottom: 40px;
}
.footer__list li {
  margin-bottom: 10px;
}
.footer__list li:last-child {
  margin-bottom: 0;
}
.footer__list a {
  font-size: 16px;
  color: #757575;
}
.footer__list a:hover {
  color: #78b0ef;
}
.footer__list--contacts a {
  font-size: 16px;
  font-family: 'Montserrat', sans-serif;
  font-weight: 500;
}
.footer__tagline {
  font-size: 14px;
  line-height: 24px;
  color: #757575;
  margin-bottom: 30px;
}
.footer__social {
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: center;
  width: 100%;
  margin-bottom: 40px;
}
.footer__social svg {
  width: 30px;
  height: auto;
  transition: opacity 0.4s ease;
}
.footer__social li {
  margin-right: 15px;
}
.footer__social li:last-child {
  margin-left: 0;
}
.footer__social a {
  font-size: 17px;
  color: #757575;
}
.footer__social a:hover {
  color: #78b0ef;
}
.footer__social a:hover svg {
  opacity: 0.7;
}
.footer__copyright {
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: flex-start;
  margin-top: 10px;
  border-top: 1px solid rgba(68,68,68,0.12);
  padding-top: 30px;
  width: 100%;
}
.footer__copyright small {
  color: #757575;
  font-size: 14px;
}
.footer__copyright small a {
  color: #757575;
}
.footer__copyright small a:hover {
  color: #78b0ef;
}
.footer__copyright ul {
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: flex-start;
  flex-wrap: wrap;
  width: 100%;
}
.footer__copyright ul li {
  margin-right: 20px;
  margin-top: 15px;
}
.footer__copyright ul li:last-child {
  margin-right: 0;
}
.footer__copyright ul a {
  color: #757575;
  font-size: 14px;
  line-height: 100%;
}
.footer__copyright ul a:hover {
  color: #78b0ef;
}
@media (min-width: 768px) {
  .footer {
    padding: 60px 0 40px;
  }
  .footer__copyright {
    margin-top: 20px;
    padding-top: 40px;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
  }
  .footer__copyright ul {
    width: auto;
    margin-top: 0;
  }
  .footer__copyright ul li {
    margin-right: 30px;
    margin-top: 0;
  }
}
@media (min-width: 1200px) {
  .footer {
    padding: 80px 0 40px;
  }
  .footer__copyright {
    margin-top: 40px;
  }
}
@-moz-keyframes arrow {
  0% {
